var countriesSelectorItem=document.querySelector(".countries-selector__item:nth-child(3)"),countriesSelectorButton=countriesSelectorItem.querySelector(".countries-selector__button");countriesSelectorButton.addEventListener("click",function(){countriesSelectorItem.classList.toggle("countries-selector__item--opened")}),window.addEventListener("keydown",function(e){countriesSelectorItem.classList.contains("countries-selector__item--opened")&&27===e.keyCode&&countriesSelectorItem.classList.remove("countries-selector__item--opened")});